import React, { Children, useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import busService from '../service.js/busService';
import userService from '../service.js/userService';
import LogOut from './LogOut';
import Popup from './Popup';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../Popup.css'



toast.configure();

const MyBookings = () => {

 const navigate = useNavigate()

 const {id} = useParams();
 const [tripId, setTripId] = useState(0)
 const [trips, setTrips] = useState([])
 const [username, setUsername] = useState('');
 const [which, setWhich] = useState(0);
 const [popup, setPopup] = useState(false);
 const [trigger, setTrigger] = useState(false);
 const [download, setDownload] = useState(false);

 useEffect(() => {
  userService.getUserById(id).then((response)=>{
    console.log()
    setUsername(response.data.username);
  }).catch(error=>{
    console.log(error);
  })
 }, [])

 const getTripsCompleted=()=>{
  setWhich(0);
  userService.getTripsCompleted(id).then((response)=>{
   setTrips(response.data);
  }).catch(error=>{
   console.log(error);
  })
 }

 const getTripsOnLive=()=>{
  setWhich(0);
  userService.getTripOnLive(id).then((response)=>{
   setTrips(response.data);
  }).catch(error=>{
   console.log(error);
  })
 }

 const getUpcomingTrips=()=>{
  userService.getUpcomingTrips(id).then((response)=>{
   setWhich(1);
   setTrips(response.data);
  }).catch(error=>{
   console.log(error);
  })
 }

 const cancekBooking=(id)=>{
  
  busService.cancelBooking(id).then((response)=>{
    toast.success("Ticket Cancelled Successfully", {autoClose :2000});
    setTrips(response.data);
    setPopup(false);
  }).catch(error=>{
    console.log(error);
  })
 }

 const ispopup=(id)=>{
  if(popup)
  {
    return (
      <div className="popup">
      <div className='popup-inner'>
          <button className='close-btn' onClick={()=> setPopup(false)}>x</button>
          <h3 className='text-danger'>Would you really want to cancel Booking?</h3>
          <div className='side-by-side1'>
            <div className='p-4'></div>
            <div className='p-4'></div>
            <div className='p-4'></div>
            <div className='p-4'></div>
            <div className='p-2'></div>
            {/* <div className='p-1'></div>
            <div className='p-1'></div>
            <div className='p-2'></div>
            <div className='p-2'></div> */}
            <button className='btn btn-success' onClick={()=>{cancekBooking(id)}}>yes</button>
            <div className='p-3'></div>
            <button className='btn btn-danger' onClick={()=>setPopup(false)}>no</button>
          </div>
      </div>
    </div>
    )
  }
 }

 const isDownload=(trip)=>{
  if(download)
  {
    return (
      <div className="popup">
      <div className='popup-inner'>
          <button className='close-btn' onClick={()=> setDownload(false)}>x</button>
          <h3 className='text-danger'>Would you want to download the Ticket?</h3>
          <div className='side-by-side1'>
            <div className='p-4'></div>
            <div className='p-4'></div>
            <div className='p-4'></div>
            <div className='p-4'></div>
            <div className='p-2'></div>
            {/* <div className='p-1'></div>
            <div className='p-1'></div>
            <div className='p-2'></div>
            <div className='p-2'></div> */}
            <button className='btn btn-success' onClick={()=>{downloadTicket(trip)}}>yes</button>
            <div className='p-3'></div>
            <button className='btn btn-danger' onClick={()=>setDownload(false)}>no</button>
          </div>
      </div>
    </div>
    )
  }
 }

 const downloadTicket = (trip)=>{
  const element = document.createElement("a");
    const nameText = "Passenger Name  :  " + trip.name;
    const busIdText = "Bus Name   :   " + trip.busId;
    const jDateText = "Journey Date   :   " + trip.date;
    const sNoText = "Seat No   :   " + trip.seatNo;
    const bPointText = "Boarding Point   :   " + trip.sPlace;
    const bTimeText = "Start Time   :   " + trip.sTime;
    const dPointText = "Destination Point   :   " + trip.ePlace;
    const dTimetext = "Arrival Time   :   " + trip.etime;
    const downloadText = nameText
    const file = new Blob([""], {
      type: "text/plain"
    });

    element.href = URL.createObjectURL(file);
    element.download = "ticket.txt";
    document.body.appendChild(element);
    element.click();
  }
 const check=(trip)=>{
  if(which == 1)
  {
    return (
      <div>
        <button className='btn btn-danger' onClick={()=>setPopup(true)}>Cancel</button>
        <button className='btn btn-success' onClick={()=>setDownload(true)}>Download</button>
        {
          ispopup(trip.id)
        }
        {
          isDownload(trip)
        }
      </div>
      )
  }
 }

 
 const checkTrips=()=>{
  if(trips.length>0)
  {
   return (
    <div>

     <div className='container-style2'>
     {
      <table className="table table-borderless">
       <thead>
        <th></th>
        <th className='text-primary text-center' style={{"fontFamily" : "-moz-initial", "fontStyle" : "-moz-initial"}}> Name </th>
        <th className='text-primary text-center' style={{"fontFamily" : "-moz-initial", "fontStyle" : "-moz-initial"}}> Bus Id </th>
        <th className='text-primary text-center' style={{"fontFamily" : "-moz-initial", "fontStyle" : "-moz-initial"}}> Date of Journey </th>
        <th className='text-primary text-center' style={{"fontFamily" : "-moz-initial", "fontStyle" : "-moz-initial"}}> Seat No </th>
        <th className='text-primary text-center' style={{"fontFamily" : "-moz-initial", "fontStyle" : "-moz-initial"}}> Boarding Point </th>
        <th className='text-primary text-center' style={{"fontFamily" : "-moz-initial", "fontStyle" : "-moz-initial"}}> Boarding Time </th>
        <th className='text-primary text-center' style={{"fontFamily" : "-moz-initial", "fontStyle" : "-moz-initial"}}> Destination </th>
        <th className='text-primary text-center' style={{"fontFamily" : "-moz-initial", "fontStyle" : "-moz-initial"}}> Arrival Time </th>
        <th></th>
       </thead>
       <tbody>
        {
         trips.map(
          trip=>(
           <tr key={ trip.id }>
            <td><img src='https://bit.ly/3dU4SGN' className='img'></img></td>
            <td className='text-center text-secondary' style={{"fontFamily" : "monospace"}}><strong> { trip.name } </strong></td>
            <td className='text-center text-secondary' style={{"fontFamily" : "monospace"}}><strong> { trip.busId } </strong></td>
            <td className='text-center text-secondary' style={{"fontFamily" : "monospace"}}><strong> { trip.date } </strong></td>
            <td className='text-center text-secondary' style={{"fontFamily" : "monospace"}}><strong> { trip.seatNo } </strong></td>
            <td className='text-center text-secondary' style={{"fontFamily" : "monospace"}}><strong> { trip.sPlace } </strong></td>
            <td className='text-center text-secondary' style={{"fontFamily" : "monospace"}}><strong> { trip.sTime } </strong></td>
            <td className='text-center text-secondary' style={{"fontFamily" : "monospace"}}><strong> { trip.ePlace } </strong></td>
            <td className='text-center text-secondary' style={{"fontFamily" : "monospace"}}><strong> { trip.eTime } </strong></td>
            <td className='text-center text-secondary' style={{"fontFamily" : "monospace"}}>
            {
              check(trip)
            }
            </td>
           </tr>
          )
         )
        }
       </tbody>
      </table>
     }
    </div>
    </div>
   )
  }
 }


  return (
    <div>
     <LogOut />
     <button className='btn btn-link' onClick={() => navigate(-1)}>Back</button>
     <div className='container'>
      <div className='text-center'>
        <img src='https://bit.ly/3KsMlxh' className='profile'></img>
        <div className='p-2'></div>
        <h4 className='text-primary'>{username}</h4>
      </div>
     </div>
      <div className='container'>
       <h2 className='text-center text-warning'>My Bookings</h2>
       <div className='side-by-side'>
        <div className='p-5'></div>
        <div>
         <button className='btn btn-warning' onClick={()=>getTripsCompleted()}>Trip Completed</button>
        </div>
        <div className='p-5'></div>
        <div className='p-5'></div>
        <div className='p-4'></div>
        <div>
         <button className='btn btn-warning' onClick={()=>getTripsOnLive()}>Trips On Live</button>
        </div>
        <div className='p-5'></div>
        <div className='p-5'></div>
        <div className='p-4'></div>
        <div>
         <button className='btn btn-warning' onClick={()=>getUpcomingTrips()}>Upcoming Trips</button>
        </div>
       </div>
       <div>
       {
        checkTrips()
       }
       </div>
       <div className='p-5'></div>
      </div>
    </div>
  )
}

export default MyBookings
