import React, { useState } from 'react'
import { toast } from 'react-toastify';
import userService from '../service.js/userService';



const ForgotPassword = () => {
 const [emailId, setEmailId] = useState('');
 const [isPresent, setIsPresent] = useState(false);
 const [password, setPassword] = useState('');
 const [confirmPassword, setConfirmPassword] = useState('');
 const [errors, setErrors] = useState({});

 



  const checkUsername=(e)=>{
   e.preventDefault();
   userService.checkUserByEmailId(emailId).then((response)=>{
    console.log(response.data);
    setIsPresent(true);
   }).catch(error=>{
    console.log(error);
    toast.error("OOPS!!! EmailId does not Exists");
   })
  }

  const changePassword=(e)=>{
   e.preventDefault();
   var count = 0;
   var dum = {};
   for(let i in errors)
   {
    if(errors[i].length == 0)
    {
     dum[i] = "*** This Field is Required ***";
     count++;
    }
   }
   if(count==0)
   {
    var newPassword = {emailId, password};
    gotoChangePassword();
   }
  }
  

  const checkPassword=(value)=>{
   setConfirmPassword(value);
   if(value != password)
   {
    errors.confirmPassword = "*** Password not matched ***";
   }
   else{
    errors.confirmPassword = "";
   }
   setErrors();
  }

 const newPasswordDetails=()=>{
  if(isPresent)
  {
   return (
    <div>
     <h2 className='text-center'>Change Password</h2>
     <div className = "form-group mb-2">
          <label className = "form-label">New Password </label>
          <input
                type = "password"
                placeholder = "new password"
                name = "password"
                className = "form-control"
                value = {password}
                onChange = {(e) => setPassword(e.target.value)}
          >
          </input>
          <small className='text-danger'>{errors.password}</small>
     </div>
     <div className = "form-group mb-2">
          <label className = "form-label"> Confirm Password </label>
          <input
                type = "password"
                placeholder = "confirm new password"
                name = "confirmPassword"
                className = "form-control"
                value = {confirmPassword}
                onChange = {(e) => checkPassword(e.target.value)}
          >
          </input>
          <small className='text-danger'>{errors.confirmPassword}</small>
     </div>
     <button className='btn btn-primary' onClick={(e)=>changePassword(e)}>Set Password</button>
    </div>
   )
  }
 }

  return (
    <div>
      <br /><br />
           <div className = "container">
                <div className = "row">
                    <div className = "card col-md-6 offset-md-3 offset-md-3">
                       <h2 className='text-center'>Forgot Password</h2>
                       <h4 className='text-center'>Find your account</h4>
                        <div className = "card-body">
                            <form>
                                <div className = "form-group mb-2">
                                    <label className = "form-label"> Email Id </label>
                                    <input
                                        type = "email"
                                        placeholder = "Enter your EmailId"
                                        name = "emailId"
                                        className = "form-control"
                                        value = {emailId}
                                        onChange = {(e) => setEmailId(e.target.value)}
                                    >
                                    </input>
                                </div>
                                <div className='text-center'>
                                 <button onClick={(e)=> checkUsername(e)}>Find</button>
                                </div>
                                {
                                 newPasswordDetails()
                                }
                            </form>

                        </div>
                    </div>
                </div>

           </div>
    </div>
  )
}

export default ForgotPassword
