import React, { useEffect, useState } from 'react'
import { useParams, Link } from 'react-router-dom';
import busService from '../service.js/busService';
import Buses from './Buses';
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css';
import userService from '../service.js/userService';
import '../App.css'
import { toBeChecked } from '@testing-library/jest-dom/dist/matchers';
import LogOut from './LogOut';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';



const Home = () => {

  // toast.dismiss();
 const [buses, setBuses] = useState([])
 const [start, setStart] = useState('');
 const [end, setEnd] = useState('');
 const [date, setDate] = useState('');
 const {id} = useParams();
 const [username, setUsername] = useState('');
 const [sample, setSample] = useState(0);
 const [selectedDate, setSelectedDate] = useState('');

 useEffect(() => {
  console.log("UserPage is Triggered")
  userService.getUserById(id).then((response)=>{
    console.log()
    setUsername(response.data.username);
  }).catch(error=>{
    console.log(error);
  })
 }, [buses])
 

 const viewBuses=()=>{
  var data = {start, end, date};
  console.log(data);
  busService.geyBuses(data).then((response)=>{
    if(response.data.length == 0)
   {
    toast.success("No buses available from this boarding and destination point", {autoClose : 2000});
   }
   
   toast.success(response.data.length + "Buses availabale");
   console.log(response.data);
   console.log("Response data is triggered");
   setBuses(response.data);
   var allBuses = response.data;
   if(response.data == buses)
   {
    console.log("yes is same");
   }
   console.log("Buses are set");
  })
 }

 const timer=()=>{
  console.log("Cancel is triggered");
    const timer = setTimeout(() => {
  }, 2000);
  return () => clearTimeout(timer);
  }

 const goToBusesPage=()=>{
  console.log("Buses");
  console.log(buses);
  console.log(buses.length);
  if(buses.length > 0)
  {
    console.log("Yes is ok");
    const viewSeats = {}
    for(let i=0;i<buses.length;i++)
    {
      var busId = buses[i].buses.busId;
      viewSeats[busId] = false;
    }
    var data = {buses, id, viewSeats};
    console.log(data);
    return (<Buses busData={data} />)
  }
  
 }

 const myBookings=()=>{
  if(sample == 0)
  {
    return <Link to={`/mybookings/${id}`} className='btn btn-link'> My Bookings </Link>
  }
 }

 const check=()=>{
  console.log("Home is Triggered")
 }

 const setDateString=(date)=>{
  let date1 = new Date(date);
  var month = date1.getMonth()+1;
  var monthString
  if(month<10)
  {
    monthString = "0"+month;
  }
  else{
    monthString = month.toString()
  }

  var day = date1.getDate();
  var dayString;
  if(day<10)
  {
    dayString = "0" + day;
  }
  else{
    dayString = day.toString();
  }

  var year = date1.getFullYear();
  var yearString = year.toString();

  var dateString =yearString+"-"+monthString+"-"+dayString;
  setDate(dateString);
  setSelectedDate(date);
 }

  return (
    <div>
      <LogOut />
      <div className='container'>
      <div className='text-center'>
        <img src='https://bit.ly/3KsMlxh' className='profile'></img>
        <div className='p-2'></div>
        <h4 className='text-warning'>{username}</h4>
        {
          myBookings()
        }
      </div>
      </div>
      <div>
        <div className='p-2'></div>
        <div className='p-2'></div>
        <div className='p-2'></div>
       <div className='container'>
        <div className="side-by-side"> 
          <div className='container'>
            <label className = "form-label text-danger"> Boarding Point </label>
              <input
                 type = "text"
                 placeholder = "Boarding Point"
                 name = "start"
                 // ref={register({ required: true })}
                 // required
                 className = "form-control"
                 value = {start}
                 onChange = {(e) => setStart(e.target.value)}
               >
              </input>
          </div>
          <div className='container'>
              <label className = "form-label text-danger"> Destination Point </label>
              <input
                    type="text"
                    placeholder="Destination Point"
                    name = "end"
                    className='form-control'
                    value={end}
                    onChange = {(e) => setEnd(e.target.value)}
                  >
              </input>
            </div>
            <div className='container'>
              <label className = "form-label text-danger"> Journey Date </label>
              {/* <input
                    type="date"
                    placeholder="Enter Journey Date"
                    name = "date"
                    className='form-control'
                    value={date}
                    onChange = {(e) => setDate(e.target.value )}
                  >
              </input> */}
              <DatePicker selected={selectedDate} onChange = {(date) => setDateString(date)}
               dateFormat="dd-MM-yyyy"
               minDate={new Date()}
               placeholderText = "dd-mm-yyyy">
              </DatePicker>
            </div>
         </div>
         <p />
         <div className='text-center'>
          <button className = "btn btn-success" onClick = {(e) => viewBuses(e)} > View Buses </button>
         </div>
        </div>
        {
         goToBusesPage()
        }
       </div>
       <div className='p-5'></div>
    </div>
  )
}

export default Home
