import React, { useState } from 'react'

const Home = () => {

 const [start, setStart] = useState('');
 const [end, setEnd] = useState('');
 const [date, setDate] = useState('');

 const viewBuses=()=>{
  var data = {start, end, date};
  console.log("Data");
  console.log(data);
 }

  return (
    <div>
      <div className='container text-center'>
       <h2 className='text-danger'>Bachi RTC Online Ticket Booking</h2>
      </div>
      <div>
       <div className='container'>
        <div className="side-by-side"> 
          <div className='container'>
            <label className = "form-label text-danger"> Boarding Point </label>
              <input
                 type = "text"
                 placeholder = "Boarding Point"
                 name = "start"
                 // ref={register({ required: true })}
                 // required
                 className = "form-control"
                 value = {start}
                 onChange = {(e) => setStart(e.target.value)}
               >
              </input>
          </div>
          <div className='container'>
              <label className = "form-label text-danger"> Destination Point </label>
              <input
                    type="text"
                    placeholder="Destination Point"
                    name = "end"
                    className='form-control'
                    value={end}
                    onChange={e => this.setEnd(e.target.value )}
                  >
              </input>
            </div>
            <div className='container'>
              <label className = "form-label text-danger"> Journey Date </label>
              <input
                    type="date"
                    placeholder="Enter Journey Date"
                    name = "date"
                    className='form-control'
                    value={date}
                    onChange={e => this.setDate(e.target.value )}
                  >
              </input>
            </div>
         </div>
         <div className='text-cente'></div>
         <button className = "btn btn-success" onClick = {(e) => viewBuses(e)} > Submit </button>
        </div>
       </div>
    </div>
  )
}

export default Home
