import React, { useState } from 'react'
import '../App.css';
// import '../Images'
// C:\Users\Brahmendra Bachi\React\bus-management\src\Images

const Buses = ({busData}) => {


 const [buses, setBuses] = useState(busData)
 var url = ""

 const isBooked=(num, mySeats, bookedSeats)=>{
  if(num in mySeats)
  {
    console.log(num, bookedSeats)
    return <button >{num}</button>
  }
  else{
    return <p className='block2'>{num}</p>
  }
 }

 const viewSeats=(vacancySeats)=>{
  if(vacancySeats.length == 0)
  {
    return <h2 className='text-danger'> No Available Seats to Book </h2>
  }

  let str = vacancySeats.substring(0, vacancySeats.length-1);

  var mySeats = str.split(",");

  console.log(mySeats)

  var bookedSeats = [];

  for(let i=1;i<26;i++)
  {
    if(i.toString() in mySeats ||  i.toString()==mySeats[mySeats.length-1])
    {
      continue;
    }
    else
    {
      bookedSeats.push(i.toString());
    }
  }

  var allSeats = [];
  allSeats.push({"id" : [1, 2, 3, 4]})
  allSeats.push({"id" : [5, 6, 7, 8]})
  allSeats.push({"id" : [9, 10, 11, 12]})
  allSeats.push({"id" : [13, 14, 15, 16]})
  allSeats.push({"id" : [17, 18, 19, 20]})

  console.log(allSeats)
  return (
    <div>
      <h2> B</h2>
      {/* <div className='container-style'>
        {
          allSeats.map(
            seat => (
              <tr key={seat.id}>
                <div className='side-by-side'>
                  {
                    isBooked(seat.id[0], mySeats, bookedSeats)
                  }
                  {
                    isBooked(seat.id[1], mySeats, bookedSeats)
                  }
                  {
                    isBooked(seat.id[2], mySeats, bookedSeats)
                  }
                  {
                    isBooked(seat.id[3], mySeats, bookedSeats)
                  }
                </div>
              </tr>
            )
          )
        }
      </div> */}
    </div>
  )
 }

 const printData=()=>{
  console.log(buses)
 }
  return (
    <div>
     <div className='p-3'></div>
     <div className='container'>
      <h2 className='text-danger'>Available Buses</h2>
      <div className='p-3'></div>
      {
       buses.map(
              bus => (
                  <tr key={bus.buses}>
                   <div className='ex1'>
                   <div className='side-by-side'>
                    <img className='img' src="https://bit.ly/3dU4SGN"></img>
                    <div className='p-3'></div>
                    <h3 className='text-warning'> { bus.buses.busId } </h3>
                    <div className='p-3'> </div>
                    <h3 className='text-warning'> { bus.buses.busName } </h3>
                    <div className='p-3'> </div>
                    <h3 className='text-info'></h3>
                    <h3 className='text-warning'> { bus.bTime } </h3>
                    <div className='p-3'> </div>
                    <h3 className='text-warning'> { bus.dTime } </h3>
                    <div className='p-3'> </div>
                    <h3 className='text-warning'> {bus.noOfSeats} seats availablle </h3>
                    <div className='p-3'></div>
                    <button className = "btn btn-danger" onClick = {() => viewSeats(bus.vacancySeats)} > View Seats </button>
                   </div>
                   </div>
                  </tr>
              )
       )
      }
     </div>
     
    </div>
  )
}

export default Buses
