import { toBeChecked } from '@testing-library/jest-dom/dist/matchers';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import busService from '../service.js/busService';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../Grid.css'
// toast.configure();

const BookSeats = ({data}) => {

 const [bookedSeats, setBookedSeats] = useState([]);
 const [allseats, setSeats] = useState(data.allSeats);
 const [tickets, setTickets] = useState(data.bookedSeats);
 const [date, setDate] = useState(data.date);
 const [busId, setBusId] = useState(data.busId);
 const [indexes, setIndexes] = useState(data.indexes);
 const [id, setId] = useState(data.id);
 const[message, setMessage] = useState('')
 const[count, setCount] = useState(bookedSeats.length)

 const navigate = useNavigate();

 useEffect(() => {
 }, [])
 

 const cancelMySeats=(num)=>{
  var dum = []
  for(let i=0;i<bookedSeats.length;i++)
  {
   dum.push(String(bookedSeats[i]));
  }
  console.log(dum);
  var ind = dum.indexOf(num);
  dum.splice(ind, ind+1);
  console.log(dum);
  setCount(count-1);
  setBookedSeats(dum);
  console.log(bookedSeats);
 }

 const editMySeats=(num)=>{
  setCount(count+1);
  var dum = []
  for(let i=0;i<bookedSeats.length;i++)
  {
   dum.push(bookedSeats[i]);
  }
  dum.push(num);
  setBookedSeats(dum);
 }
 
 const isBooked=(num)=>{
  if(tickets.includes(num, 0))
  {
    console.log(tickets)
  //  return <button className='block1'>&nbsp;&nbsp;&nbsp;</button>
    return (
     <div>
      <div className='Grid1'>
          {/* <p></p> */}
      </div>
     </div>
     )
  }
  else if(bookedSeats.includes(num,0)){
    // return <button className='block2' onClick={()=> cancelMySeats(num)}>{ num }</button>
    return (
      <div>
        <button  className='buttonSize2' onClick={()=> cancelMySeats(num)}>
          <p className='textCenter'>{num}</p>
        </button>
      </div>
    )
  }
  else
  {
  //  return <button className='button1' onClick={()=> editMySeats(num)}>&nbsp;&nbsp;&nbsp;</button>
  return (
    <div>
       <button  className='buttonSize1' onClick={()=> editMySeats(num)}>
          <p ></p>
        </button>
    </div>
   )
  }
 }

 const check=()=>{
 }

 const Book=()=>{
  console.log("Length :"+count)
  if(count > 1){
   return <button className = "btn btn-danger" onClick = {() => BookSeatsSubmit()} > Book {count} Seats </button>
  }
  if(count == 1)
  {
    return <button className = "btn btn-danger" onClick = {() => BookSeatsSubmit()} > Book {count} Seat </button> 
  }
 }

 const BookSeatsSubmit=()=>{
  console.log("Book Seats Submitted")
  var data = {bookedSeats, date, busId, indexes, id, count}
  console.log(data);
  navigate(`/passenger-details/${id}`,{state:{Data : data}});

 }

 const timer=()=>{
  console.log("Cancel is triggered");
  toast.success("Tickets Booked Successfully");
    const timer = setTimeout(() => {
      navigate(`/`)
  }, 2000);
  return () => clearTimeout(timer);
  }


  return (
    <div>
      <h2 className='text-center text-danger'> Book Your Seats </h2>
      <h2 className='text-center text-primary'>{message}</h2>
       <div className='container-style'>
        <div className='mb-2'>
        <div className='p-1'></div>
        {
         check()
        }
        {
          allseats.map(
            seat => (
              <tr key={seat.id}>
                <div className='side-by-side'>
                  <div className='p-1'></div>
                  {
                    isBooked(seat.id[0])
                  }
                  <div className='p-1'></div>
                  {
                    isBooked(seat.id[1])
                  }
                  <div className='p-4'></div>
                  {
                    isBooked(seat.id[2])
                  }
                  <div className='p-1'></div>
                  {
                    isBooked(seat.id[3])
                  }
                </div>
                {/* <div className='p-1'></div> */}
              </tr>
            )
          )
        }
        {/* <div className='p-1'></div> */}
        <div className='side-by-side'>
          <div className='p-1'></div>
         {
           isBooked('21')
         }
         <div className='p-1'></div>
         {
           isBooked('22')
         }
         <div className='p-1'></div>
         {
           isBooked('23')
         }
         <div className='p-1'></div>
         {
           isBooked('24')
         }
         <div className='p-1'></div>
         {
           isBooked('25')
         }
        </div>
      </div>
      <div className='p-1'></div>
       <div className='text-center'>
        {
         Book()
        }
        <div className='p-1'></div>
        {/* <div className='p-5'></div> */}
       </div>
      </div>
      <div className='p-2'></div>
    </div>
  )
}

export default BookSeats
