import React, { useState } from 'react'
import '../Popup.css'

const Popup = (props) => {

  const setTrigger=()=>{
    props.trigger = fals
  }
  return ( props.trigger )?(
   <div className="popup">
    <div className='popup-inner'>
     <button className='close-btn' onClick={()=> setTrigger() }>Close</button>
      { props.children }
    </div>
    </div>   
  ) : ""
}

export default Popup
