import { toBeChecked } from '@testing-library/jest-dom/dist/matchers';
import React, { useEffect, useState } from 'react'
import busService from '../service.js/busService';

const BookSeats = ({data}) => {

 const [bookedSeats, setBookedSeats] = useState([]);
 const[allseats, setSeats] = useState(data.allSeats);
 const[tickets, setTickets] = useState(data.bookedSeats)

 useEffect(() => {
 }, [])
 

 const cancelMySeats=(num)=>{
  console.log("num "+ num);
  var dum = []
  for(let i=0;i<bookedSeats.length;i++)
  {
   dum.push(String(bookedSeats[i]));
  }
  console.log(dum);
  var ind = dum.indexOf(num);
  dum.splice(ind);
  console.log(dum)
  setBookedSeats(dum);
  console.log(bookedSeats);
 }
 const editMySeats=(num)=>{
  var dum = []
  for(let i=0;i<bookedSeats.length;i++)
  {
   dum.push(bookedSeats[i]);
  }
  dum.push(num);
  setBookedSeats(dum);
 }
 
 const isBooked=(num)=>{
  console.log("Book is triggered");
  console.log(num)
  console.log(bookedSeats)
  
  if(tickets.includes(num, 0) || bookedSeats.includes(num,0))
  {
   console.log("My Books");
   return <button className='block1' onClick={()=> cancelMySeats(num)}>&nbsp;&nbsp;&nbsp;</button>
  }
  else
  {
   return <button className='button' onClick={()=> editMySeats(num)}>&nbsp;&nbsp;&nbsp;</button>
  }
 }

 const check=()=>{
  console.log("Check");
 }

 const Book=()=>{
  console.log("Length :"+bookedSeats.length)
  if(bookedSeats.length>0){
   return <button className = "btn btn-danger" onClick = {() => BookSeatsSubmit()} > Book {bookedSeats.length} Seats </button>
  }
 }

 const BookSeatsSubmit=()=>{

  busService.bookBusTickets(bookedSeats).then((response)=>{
   console.log("")
  })
  
 }
  return (
    <div>
      <h2 className='text-center text-danger'> Book Your Seats </h2>
       <div className='container-style'>
        <div className='p-2'></div>
        {
         check()
        }
        {
          allseats.map(
            seat => (
              <tr key={seat.id}>
                <div className='side-by-side'>
                  {
                    isBooked(seat.id[0])
                  }
                  <div className='p-2'></div>
                  {
                    isBooked(seat.id[1])
                  }
                  <div className='p-2'></div>
                  <div className='p-2'></div>
                  <div className='p-2'></div>
                  {
                    isBooked(seat.id[2])
                  }
                  <div className='p-2'></div>
                  {
                    isBooked(seat.id[3])
                  }
                </div>
                <div className='p-1'></div>
              </tr>
            )
          )
        }
        <div className='p-1'></div>
        <div className='side-by-side'>
         {
           isBooked('21')
         }
         <div className='p-2'></div>
         {
           isBooked('22')
         }
         <div className='p-2'></div>
         {
           isBooked('23')
         }
         <div className='p-2'></div>
         {
           isBooked('24')
         }
         <div className='p-2'></div>
         {
           isBooked('25')
         }
        </div>
      </div>
      <div className='p-3'></div>
       <div className='text-center'>
        {
         Book()
        }
       </div>
      </div>
  )
}

export default BookSeats
