import React, { useState } from 'react'
import '../Popup.css'

const Popup = ({data}) => {
  const [ispop, setIspop] = useState(data.value)
  console.log("Props is Triggered");
  return ( props.trigger)?(
   <div className="popup">
    <div className='popup-inner'>
     <button className='close-btn' onClick={()=>setIspop()}>Close</button>
      { props.children }
    </div>
    </div>   
  ) : ""
}

export default Popup
