import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import busService from '../service.js/busService';
import userService from '../service.js/userService';
import LogOut from './LogOut';
import Popup from './Popup';
import {toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
toast.configure();


const MyBookings = () => {

 const navigate = useNavigate()

 const {id} = useParams();
 const [trips, setTrips] = useState([])
 const [username, setUsername] = useState('');
 const [which, setWhich] = useState(0);
 const [popup, setPopup] = useState(false);

 useEffect(() => {
  console.log("UserPage is Triggered")
  userService.getUserById(id).then((response)=>{
    console.log()
    setUsername(response.data.username);
  }).catch(error=>{
    console.log(error);
  })
 }, [])

 const getTripsCompleted=()=>{
  setWhich(0);
  userService.getTripsCompleted(id).then((response)=>{
   setTrips(response.data);
  }).catch(error=>{
   console.log(error);
  })
 }

 const getTripsOnLive=()=>{
  setWhich(0);
  userService.getTripOnLive(id).then((response)=>{
   setTrips(response.data);
  }).catch(error=>{
   console.log(error);
  })
 }

 const getUpcomingTrips=()=>{
  userService.getUpcomingTrips(id).then((response)=>{
   setWhich(1);
   setTrips(response.data);
  }).catch(error=>{
   console.log(error);
  })
 }

 const cancekBooking=(id)=>{
  busService.cancelBooking(id).then((response)=>{
    toast.success("Ticket Cancelled Successfully");
    setTrips(response.data);
  }).catch(error=>{
    console.log(error);
  })
 }

 const check=(id)=>{
  console.log("Which value :"+which);
  if(which == 1)
  {
    return (
      <div>
        <button className='btn btn-danger' onClick={()=>setPopup(true)}>Cancel</button>
        <Popup trigger={popup}>
          <h3 className='text-danger'>Would you really want to cancel Booking?</h3>
          <div className='side-by-side'>
            <button className='btn btn-success text-cente'>yes</button>
          </div>
          </Popup >
      </div>
      )
  }
 }

 
 const checkTrips=()=>{
  if(trips.length>0)
  {
   return (
    <div>

     <div className='container-style2'>
     {
      <table className="table table-dark table-striped">
       <thead>
        <th></th>
        <th className='text-primary'> Name </th>
        <th className='text-primary'> Bus Id </th>
        <th className='text-primary'> Date of Journey </th>
        <th className='text-primary'> Seat No </th>
        <th className='text-primary'> Boarding Point </th>
        <th className='text-primary'> Start time </th>
        <th className='text-primary'> Destination </th>
        <th className='text-primary'> End Time </th>
        <th></th>
       </thead>
       <tbody>
        {
         trips.map(
          trip=>(
           <tr key={ trip.id }>
            <td><img src='https://bit.ly/3dU4SGN' className='img'></img></td>
            <td> { trip.name } </td>
            <td> { trip.busId } </td>
            <td> { trip.date } </td>
            <td> { trip.seatNo } </td>
            <td> { trip.sPlace } </td>
            <td> { trip.sTime } </td>
            <td> { trip.ePlace } </td>
            <td> { trip.eTime } </td>
            <td>
              {
                check(trip.id)
              }
            </td>
           </tr>
          )
         )
        }
       </tbody>
      </table>
     }
    </div>
    </div>
   )
  }
 }


  return (
    <div>
     <LogOut />
     <button className='btn btn-link' onClick={() => navigate(-1)}>Back</button>
     <div className='container'>
      <div className='text-center'>
        <img src='https://bit.ly/3KsMlxh' className='profile'></img>
        <div className='p-2'></div>
        <h4 className='text-primary'>{username}</h4>
      </div>
     </div>
      <div className='container'>
       <h2 className='text-center text-warning'>My Bookings</h2>
       <div className='side-by-side'>
        <div className='p-5'></div>
        <div>
         <button className='btn btn-warning' onClick={()=>getTripsCompleted()}>Trip Completed</button>
        </div>
        <div className='p-5'></div>
        <div className='p-5'></div>
        <div className='p-4'></div>
        <div>
         <button className='btn btn-warning' onClick={()=>getTripsOnLive()}>Trips On Live</button>
        </div>
        <div className='p-5'></div>
        <div className='p-5'></div>
        <div className='p-4'></div>
        <div>
         <button className='btn btn-warning' onClick={()=>getUpcomingTrips()}>Upcoming Trips</button>
        </div>
       </div>
       <div>
       {
        checkTrips()
       }
       </div>
       <div className='p-5'></div>
      </div>
    </div>
  )
}

export default MyBookings
