import { toBeChecked } from '@testing-library/jest-dom/dist/matchers';
import React, { useState } from 'react'

const BookSeats = ({data}) => {


 const [mySeats, setMySeats] = useState(data.mySeats);
 const [bookedSeats, setBookedSeats] = useState(data.bookedSeats);
 const[allseats, setSeats] = useState(data.allSeats);

 const editMySeats=(num)=>{
  console.log("num", num)
  bookedSeats.push(num);
  setBookedSeats(bookedSeats);
  var ind = mySeats.index(num);
  console.log
  setMySeats(mySeats);
 }
 
 const isBooked=(num, mySeats, bookedSeats)=>{
  if(num in mySeats)
  {
    console.log(num, bookedSeats)
    return <button onClick={()=> editMySeats(num)}>{num}</button>
  }
  else{
   console.log("My Books");
    return <p className='block2'>{num}</p>
  }
 }

  return (
    <div>
      <h2 className='text-center text-danger'>Book Your Seats</h2>
       <div className='container-style'>
        {
          allseats.map(
            seat => (
              <tr key={seat.id}>
                <div className='side-by-side'>
                  {
                    isBooked(seat.id[0], mySeats, bookedSeats)
                  }
                  <div className='p-3'></div>
                  {
                    isBooked(seat.id[1], mySeats, bookedSeats)
                  }
                  <div className='p-3'></div>
                  <div className='p-3'></div>
                  {
                    isBooked(seat.id[2], mySeats, bookedSeats)
                  }
                  <div className='p-3'></div>
                  {
                    isBooked(seat.id[3], mySeats, bookedSeats)
                  }
                </div>
              </tr>
            )
          )
        }
      </div>
    </div>
  )
}

export default BookSeats
