import React from 'react'

const ShowSeats = (props) => {

 const isBooked=(num)=>{
  if(tickets.includes(num, 0))
  {
    return (
     <div>
      <div className='Grid1'></div>
     </div>
     )
  }
  else if(bookedSeats.includes(num,0)){
    return (
      <div>
        <button  className='buttonSize2' onClick={()=> cancelMySeats(num)}>
          <p className='textCenter'>{num}</p>
        </button>
      </div>
    )
  }
  else
  {
  return (
    <div>
       <button  className='buttonSize1' onClick={()=> editMySeats(num)}>
          <p ></p>
        </button>
    </div>
   )
  }
 }

  return (
    <div>
      {
          allseats.map(
            (seat, index) => (
              <tr key={index}>
                <div className='side-by-side'>
                  <div className='p-1'></div>
                  {
                    isBooked(seat[0])
                  }
                  <div className='p-1'></div>
                  {
                    isBooked(seat[1])
                  }
                  <div className='p-4'></div>
                  {
                    isBooked(seat[2])
                  }
                  <div className='p-1'></div>
                  {
                    isBooked(seat[3])
                  }
                </div>
                {/* <div className='p-1'></div> */}
              </tr>
            )
          )
        }
    </div>
  )
}

export default ShowSeats
